items.map:
function(doc, meta) 
{
  if (doc.products) 
  {
    for(i=0; i < doc.products.length; i++) 
    {
      if (doc.products[i].items)
      {
        for (j=0; j < doc.products[i].items.length; j++)
        {
      	  emit(doc.products[i].items[j].id, null);
        }
      }
    }
  }
}

all.map:
function (doc, meta) {
  if (doc.type && doc.type == "category") {
    emit(meta.id, null);
  }
}

